package org.matt.interceptor;import com.opensymphony.xwork2.ActionInvocation;import com.opensymphony.xwork2.interceptor.Interceptor;import org.apache.shiro.SecurityUtils;import org.apache.shiro.subject.Subject;import org.matt.login.ShiroBaseAction;public class ShiroUserInterceptor implements Interceptor {	private static final long serialVersionUID = 1L;	public void destroy() {	}	public void init() {	}	public String intercept(ActionInvocation actionInvocation) throws Exception {		if ((actionInvocation.getAction() instanceof ShiroBaseAction)) {			Subject shiroUser = SecurityUtils.getSubject();			actionInvocation.getStack().setValue("shiroUser", shiroUser);		}		return actionInvocation.invoke();	}}